package model;

public class RockData {

	private float x;
	private float y;
	private int rotation;
	
	public RockData() {
		// TODO Auto-generated constructor stub
	}
	
	public RockData(float x, float y, int rotation) {
		this.x = x;
		this.y = y;
		this.rotation = rotation;
	}

	public float getX() {
		return x;
	}

	public float getY() {
		return y;
	}
	
	public int getRotation() {
		return rotation;
	}
	
	
}
